import React, { useState, useEffect } from 'react';
import { StyleSheet, View, Text, FlatList, ScrollView } from 'react-native';
import { getStates } from '../services/placesService';
import StateCard from '../components/StateCard';

const StatesGrid = ({ navigation }) => {
  const [states, setStates] = useState([]);

  useEffect(() => {
    getStates().then(setStates);
  }, []);

  return (
    <ScrollView style={styles.container}>
      <Text style={styles.heading}>States of India</Text>
      <FlatList
        data={states}
        renderItem={({ item }) => (
          <StateCard
            state={item}
            onPress={() => navigation.navigate('PlacesGrid', { stateName: item.name })}
          />
        )}
        numColumns={2}
        keyExtractor={(item) => item.name}
      />
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    padding: 10,
  },
  heading: {
    fontSize: 24,
    fontWeight: 'bold',
    textAlign: 'center',
    marginVertical: 10,
  },
});

export default StatesGrid;
