import React, { useState, useEffect } from 'react';
import { StyleSheet, View, Text, Image } from 'react-native';
import { getPlaceByName } from '../services/placesService';

const PlaceDetails = ({ route }) => {
  const { placeName } = route.params;
  const [place, setPlace] = useState(null);

  useEffect(() => {
    getPlaceByName(placeName).then(setPlace);
  }, [placeName]);

  if (!place) {
    return (
      <View style={styles.container}>
        <Text>Loading place details...</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <Text style={styles.heading}>{place.name}</Text>
      <Image source={{ uri: place.image }} style={styles.image} />
      <Text>{place.address}</Text>
      <Text>{place.contact}</Text>
      <Text>{place.historical_info}</Text>
      <Text>{place.tourism_info}</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    padding: 10,
  },
  heading: {
    fontSize: 24,
    fontWeight: 'bold',
    textAlign: 'center',
    marginVertical: 10,
  },
  image: {
    width: 300,
    height: 200,
    borderRadius: 10,
    marginBottom: 10,
  },
});

export default PlaceDetails;
