import React, { useState, useEffect } from "react";
import { View, Text, Image, StyleSheet, ScrollView } from "react-native";
import MapView, { Marker } from "react-native-maps";
import { getPlaceByName } from "../services/placesService";
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
} from "react-native-responsive-screen";

const PlaceDetails = ({ route }) => {
  const { placeName } = route.params;
  const [place, setPlace] = useState(null);

  useEffect(() => {
    getPlaceByName(placeName).then(setPlace);
  }, [placeName]);

  if (!place) {
    return (
      <View style={styles.container}>
        <Text style={styles.loadingText}>Loading place details...</Text>
      </View>
    );
  }

  return (
    <ScrollView style={styles.container}>
      <Text style={styles.heading}>{place.name}</Text>
      <Image source={{ uri: place.image }} style={styles.image} />

      <View style={styles.infoContainer}>
        <View style={styles.rowContainer}>
          <Text style={styles.label}>Address:</Text>
          <Text style={styles.value}>{place.address}</Text>
        </View>
        <View style={styles.rowContainer}>
          <Text style={styles.label}>Contact:</Text>
          <Text style={styles.value}>{place.contact}</Text>
        </View>
        <View style={styles.rowContainer}>
          <Text style={styles.label}>Historical Info:</Text>
          <Text style={styles.value}>{place.historical_info}</Text>
        </View>
        <View style={styles.rowContainer}>
          <Text style={styles.label}>Tourism Info:</Text>
          <Text style={styles.value}>{place.tourism_info}</Text>
        </View>
      </View>

      <MapView
        style={styles.map}
        initialRegion={{
          latitude: place.latitude,
          longitude: place.longitude,
          latitudeDelta: 0.05,
          longitudeDelta: 0.05,
        }}
      >
        <Marker
          coordinate={{ latitude: place.latitude, longitude: place.longitude }}
          title={place.name}
        />
      </MapView>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#f8f9fa",
    paddingHorizontal: wp(4),
  },
  heading: {
    fontSize: wp(6),
    fontWeight: "bold",
    textAlign: "center",
    marginVertical: hp(2),
    color: "#343a40",
  },
  image: {
    width: "100%",
    height: hp(25),
    borderRadius: wp(3),
    marginBottom: hp(2),
  },
  infoContainer: {
    backgroundColor: "#fff",
    borderRadius: wp(3),
    padding: wp(4),
    marginBottom: hp(2),
    shadowColor: "#000",
    shadowOpacity: 0.1,
    shadowRadius: wp(2),
    shadowOffset: { width: 0, height: 2 },
    elevation: 3,
  },
  rowContainer: {
    flexDirection: "row",
    marginBottom: hp(1.5),
  },
  label: {
    fontWeight: "bold",
    fontSize: wp(4),
    color: "#343a40",
    width: "30%",
  },
  value: {
    fontSize: wp(4),
    color: "#495057",
    width: "70%",
  },
  map: {
    width: "100%",
    height: hp(30),
    borderRadius: wp(3),
    marginBottom: hp(2),
  },
  loadingText: {
    textAlign: "center",
    fontSize: wp(5),
    marginVertical: hp(2),
    color: "#6c757d",
  },
});

export default PlaceDetails;
